import fetch from "isomorphic-unfetch";
// eslint-disable-next-line import/no-extraneous-dependencies
import css from "styled-jsx/css";
import Profile from "../../components/Profile";
import Repositories from "../../components/Repositories";

const style = css`
  .user-contents-wrapper {
    padding: 20px;
    display: flex;
  }
`;

const name = ({ user, repos }) => {
  return (
    <div className="user-contents-wrapper">
      <Profile user={user} />
      <Repositories user={user} repos={repos} />
      <style jsx>{style}</style>
    </div>
  );
};

export const getServerSideProps = async ({ query }) => {
  const { name, page = "1" } = query;
  try {
    let user;
    let repos;

    const userRes = await fetch(`https://api.github.com/users/${name}`, {
      headers: {
        Authorization: "token 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",
      },
    });
    if (userRes.status === 200) {
      user = await userRes.json();
    } else {
      throw Error(userRes.statusText);
    }
    const repoRes = await fetch(
      `https://api.github.com/users/${name}/repos?sort=updated&page=${page}&per_page=10`,
      {
        headers: {
          Authorization: "token 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",
        },
      }
    );
    if (repoRes.status === 200) {
      repos = await repoRes.json();
    } else {
      throw Error(userRes.statusText);
    }
    return { props: { user, repos } };
  } catch (e) {
    console.log(e.message);
    return { props: {} };
  }
};

export default name;
